-- ----------------------------
-- Table structure for `hd_order`
-- ----------------------------
DROP TABLE IF EXISTS `hd_order`;
CREATE TABLE `hd_order` (
  `order_id` int(11) NOT NULL auto_increment,
  `order_number` varchar(15) NOT NULL COMMENT '订单号',
  `member_name` varchar(15) NOT NULL COMMENT '会员名称',
  `goods_name` varchar(30) NOT NULL COMMENT '商品名称',
  `goods_id` int(8) NOT NULL COMMENT '商品名称',
  `goods_color` varchar(5) NOT NULL COMMENT '商品颜色',
  `goods_size` varchar(5) NOT NULL COMMENT '商品尺寸',
  `goods_price` double(8,2) NOT NULL COMMENT '商品价格',
  `goods_sum` int(9) NOT NULL COMMENT '数量',
  `goods_total` float NOT NULL COMMENT '统计',
  `is_fukuan` tinyint(2) NOT NULL COMMENT '是否支付',
  `to_name` varchar(8) NOT NULL COMMENT '收件人',
  `to_phone` varchar(20) NOT NULL COMMENT '电话',
  `to_address` varchar(80) NOT NULL COMMENT '地址',
  `to_youbian` varchar(8) NOT NULL COMMENT '邮编',
  `to_message` varchar(80) NOT NULL COMMENT '发货信息',
  `to_fa` tinyint(2) NOT NULL COMMENT '是否发货',
  `xia_time` datetime default NULL,
  `is_ping` tinyint(1) NOT NULL default '0',
  `to_time` datetime NOT NULL COMMENT '发货时间',
  PRIMARY KEY  (`order_id`)
) ENGINE=InnoDB AUTO_INCREMENT=17 DEFAULT CHARSET=utf8;

