DROP TABLE IF EXISTS `hd_value`;
CREATE TABLE `hd_value` (
  `value_id` int(11) NOT NULL auto_increment,
  `member_name` varchar(15) NOT NULL COMMENT '会员名',
  `member_level` smallint(5) NOT NULL COMMENT '会员等级',
  `value_con` text NOT NULL COMMENT '评论内容',
  `goods_id` int(8) NOT NULL COMMENT '商品ID',
  `goods_name` varchar(15) NOT NULL COMMENT '商品名称',
