  `value_status` tinyint(2) NOT NULL COMMENT '评价状态',
  `value_time` datetime NOT NULL COMMENT '评价时间',
  PRIMARY KEY  (`value_id`)
) ENGINE=InnoDB AUTO_INCREMENT=6 DEFAULT CHARSET=utf8;

